WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")} 

try { 
	var personContactId = sforce.apex.execute("MC4SF.Util", "getPersonContactId", {accountId: '{!Account.Id}'}); 
	if (personContactId == '') {
		alert('This feature is only applicable to Person Accounts.');
	} else {
		top.location.href = '/apex/MC4SF__SubscriptionsContact?id=' + personContactId;
	}
} catch (error) { 
	if (error.faultcode == 'sf:INSUFFICIENT_ACCESS') { 
		alert('You do not have permission to perform this function.'); 
	} else { 
		alert(error); 
	} 
}
